function NumToPersian(a,b){"undefined"==typeof b&&(b=0);var c=[];h=a.length;var d=[];if(h>3){var e=h%3,f=0;e>0&&d.push(a.substring(0,e));for(var g=e;h>g;g++)f%3==0&&d.push(a.substring(g,g+3)),f++;var h=d.length,i=[];for(m=0;h>m;m++){var j=NumToPersian(d[m],2);"[object Array]"===Object.prototype.toString.call(j)&&(k=j.join(andd),""!=persian[3][h-m]&&(k+=persian[3][h-m]),i.push(k))}return i.join(andd)}var l=a.split("");if(0==parseInt(a))return 0==b?zero:"";if(3==h){if(0==parseInt(l[0]+l[1]))c.push(persian[0][l[2]]);else for(var m=0;m<l.length;m++)if(0!=l[m]){if(0==m&&c.push(persian[2][l[m]]),1==m){var n=parseInt(l[m]+l[m+1]);n>19?c.push(persian[1][l[m]]):c.push(persian[4][l[m+1]])}2==m&&0!=l[m]&&c.push(persian[0][l[m]])}}else if(2==h)if(0!=l[0]&&0==l[1])c.push(persian[1][l[0]]);else if(0!=l[1]&&0!=l[0]&&parseInt(l[0]+l[1])>20)c.push(persian[1][l[0]]),c.push(persian[0][l[1]]);else{if(!(parseInt(l[0]+l[1])<20))return"";c.push(persian[4][l[1]])}else 1==h&&c.push(persian[0][l[0]]);return 0===b?c.join(andd):c}var andd=" و ",zero="صفر",persian=[["","یك","دو","سه","چهار","پنج","شش","هفت","هشت","نه"],["","ده","بیست","سی","چهل","پنجاه","شصت","هفتاد","هشتاد","نود"],["","یكصد","دویست","سیصد","چهارصد","پانصد","ششصد","هفتصد","هشتصد","نهصد"],["",""," هزار "," میلیون "," میلیارد "," تریلیون "],["ده","یازده","دوازده","سیزده","چهارده","پانزده","شانزده","هفده","هجده","نوزده"]];String.prototype.toPersian=function(){return NumToPersian(this,0)};
